import React from "react";

const Films = ({ films }) => {
  return (
    <div>
      <h1>Films</h1>
      {films ? (
        <div>
          <ul>
            {films.map((film) => {
              // console.log(film);
              return (
                <li>
                  <div>
                    <h3>{film.title}</h3>
                    <p>({film.year})</p>
                    <p>Director: {film.director}</p>
                    <img src={film.img} />
                    <p>Genre: {film.genre}</p>
                  </div>
                </li>
              );
            })}
          </ul>
        </div>
      ) : (
        <></>
      )}
    </div>
  );
};

export default Films;
