import React from "react";

const Films = ({ films }) => {
  return (
    <div>
      <h1>Films</h1>
      {films ? (
        <div>
          <form>
            <input type="text" className="films-search-input" />
            <select></select>
          </form>
          <ul>
            {films.map((film) => {
              // console.log(film);
              return (
                <li>
                  <div className="films-card-container">
                    <h3>{film.title}</h3>
                    <p>({film.year})</p>
                    <img src={film.img} />
                    <p>Director: {film.director}</p>
                    <p>Genre: {film.genre}</p>
                    <p>{film.description}</p>
                    <p>${film.price}</p>
                  </div>
                </li>
              );
            })}
          </ul>
        </div>
      ) : (
        <></>
      )}
    </div>
  );
};

export default Films;
